import { StyleSheet, Text, View,Image } from "react-native";
import React from "react";

export default function BottomBar() {
  return (
    <View style={styles.bottomBar}>
      <View style={styles.item} >
      <Image source={require("../assets/home.png")} style={{height:24,width:24}} />
      <Text>Home</Text>
      </View>
      <View style={styles.item} >
      <Image source={require("../assets/youtube-music.png")} style={{height:24,width:24}} />
      <Text>Shorts</Text>
      </View>
      <View style={styles.item} >
      <Image source={require("../assets/video-playlist.png")} style={{height:24,width:24}} />
      <Text>subscriptions</Text>
      </View>
      <View style={styles.item} >
      <Image source={require("../assets/video-gallery.png")} style={{height:24,width:24}} />
      <Text>Library</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  bottomBar: {
    position:"absolute",
    height:48,
    bottom:0,
    width:"100%",
    backgroundColor:"#ffff",
    display:"flex",
    flexDirection:"row",
    justifyContent:"space-around",
    alignItems:"center",

  },
  item:{
    display:"flex",
    justifyContent:'center',
    alignItems:"center"
  }
});
