import {
  SafeAreaView,
  SafeAreaViewComponent,
  StyleSheet,
  Text,
  View,
  Platform,
  StatusBar,
  Image,
  Button,
  TouchableOpacity,
  ScrollView,
  Pressable
} from "react-native";
import React from "react";

export default function Topbar() {
  return (
    <View style={styles.scrollin}>
      <StatusBar backgroundColor={"#fff"} barStyle="dark-content" />
      <View style={styles.topbar}>
        <View>
          <Image
            style={styles.img}
            source={require("../assets/logo.png")}
            resizeMode={"cover"}
          />
        </View>
        <View
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <View style={{ padding: 12 }}>
            <Image
              style={{ width: 23, height: 23 }}
              source={require("../assets/search--v2.png")}
            />
          </View>
          <View style={{ padding: 12 }}>
            <Image
              style={{ width: 28, height: 28, borderRadius: 50 }}
              source={require("../assets/unnamed.jpg")}
            />
          </View>
        </View>
      </View>

      <View style={styles.Qiuckbtn}>
        <ScrollView horizontal={true}>
          <TouchableOpacity style={[styles.btnRound, styles.space,{borderRadius: 5,marginLeft:12}]}>
            <Image
              style={{ width: 20, height: 20, marginRight: 4 }}
              source={require("../assets/compass.png")}
            />
            <Text>Explore</Text>
          </TouchableOpacity>
          <View
            style={[
              styles.space,
              {
                backgroundColor: "#DCDCDC",
                height: 32,
                width: 1,
              },
            ]}
          />
          <TouchableOpacity style={[styles.btnRound, styles.space,{backgroundColor:"#606060"}]}>
            <Text style={{color:"white"}}>All</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.btnRound, styles.space]}>
            <Text>Music</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.btnRound, styles.space]}>
            <Text>Bodybuilding</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.btnRound, styles.space]}>
            <Text>Motivation</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.btnRound, styles.space]}>
            <Text>Social media marketing</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.btnRound, styles.space]}>
            <Text>KSI</Text>
          </TouchableOpacity>
        </ScrollView>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  scrollin:{
    position:"absolute",
    top:0,
    backgroundColor:"white"
  },
  scrollout:{
    position:"relative",
    top:-100,
    backgroundColor:"white"
  },
  topbar: {
    height: 48,
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
  },
  Qiuckbtn: {
    height: 48,
    borderBottomColor:"lightgray",
    borderBottomWidth:1
  },
  img: {
    height: 50,
    width: 122,
  },
  space: {
    marginTop: 8,
    marginBottom: 8,
    marginRight: 12,
  }, 
  btnRound: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#F5F5F5",
    height: 32,
    paddingLeft:12,
    paddingRight:12,
    borderRadius: 25,
    borderColor:'#606060',
    borderWidth:0.2,
  },
});
