import { ScrollView, StyleSheet, Text, View,Image } from "react-native";
import React, { useState } from "react";
import { useEffect } from "react";
// import { fetchApi } from "../util/fetchApi";
export default function Content() {

   const [img, setimg] = useState("")
  const [selectedCategory, setselectedCategory] = useState('New')
  useEffect(()=>{
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': '56d0c66f6emshceb236c6fcc2a79p107c0bjsn9609b15c22a0',
        'X-RapidAPI-Host': 'youtube-v31.p.rapidapi.com'
      }
    };

    fetch(`https://youtube-v31.p.rapidapi.com/search?part=snippet&q=${selectedCategory}&type=video&maxResults=50`, options)
    // fetchApi(`search?part=snippet&q=${selectedCategory}`)
    .then(Response => Response.json())
    .then(data => {
      console.log(data.items[0].snippet.thumbnails.high);
      setimg(data.items[0].snippet.thumbnails.high.url);
  })
  },[selectedCategory]);
  return (
    <View>
      <ScrollView>
        <View style={styles.video}>
        <Image
            style={styles.image}
            source={{uri:img}}
            resizeMode={"cover"}
          />
        </View>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  video:{
    backgroundColor:"black",
  },
  image:{
    height:290,

  }
});
